mapper.select:
select
from request_main where id in
#{item}

	select * FROM(
			select ''as requestid,p.packno as code,'' as
			boxcode,''as
			requesttime,''as requeststatus,''as updatetime,''
			as speed,'' as duetime,null as differtime,''as
			totalamount,'' as sendtype,'1' as flag,''as status,NULL AS paydiffertime,
		    '--' AS payinfo,'' as entityType 
			from logistics_pack p where
			p.cutstatus='0' and
			p.receivestatus!='1' and
			p.ownid=#{ownid,jdbcType=INTEGER}
		) dd 
	where 1=1


          and code=#{billnumber,jdbcType=INTEGER}    
          and flag=#{flag,jdbcType=CHAR}


	LIMIT #{page,jdbcType=INTEGER},#{pagesize,jdbcType=INTEGER}

select * FROM( select t.id as requestid,t.requestcode as code,t.boxcode,t.requesttime,t.requeststatus,t.updatetime,t.speed, t.duetime,time_to_sec(timediff(t.duetime,now())) as differtime,t.totalamount,t.sendtype, '0' as flag,t.internalstates as status, TIME_TO_SEC( TIMEDIFF( (SELECT DATE_FORMAT( MAX(p.createdate), '%Y-%m-%d %H:%i:%S' ) FROM pay_journal p WHERE FIND_IN_SET(t.requestcode, p.requestcode)), t.duetime ) ) * (- 1) AS paydiffertime, ( CASE WHEN t.paystatus = '1' THEN IFNULL( (SELECT DATE_FORMAT( MAX(p.createdate), '%Y-%m-%d %H:%i:%S' ) FROM pay_journal p WHERE FIND_IN_SET(t.requestcode, p.requestcode)), "已支付" ) ELSE "--" END ) payinfo,t.entitytype as entityType from request_main t where t.ownid=#{ownid,jdbcType=INTEGER} AND requeststatus !='7' AND requeststatus > '0'
AND t.entitytype=#{entityType,jdbcType=INTEGER}
ORDER BY time_to_sec(timediff(now(), updatetime)) desc,code ) dd where 1=1
and code=#{billnumber,jdbcType=INTEGER} and flag=#{flag,jdbcType=CHAR}
LIMIT #{page,jdbcType=INTEGER},#{pagesize,jdbcType=INTEGER}

	select count(*) FROM(
	select t.requestcode as
	code,t.boxcode,t.requesttime,t.requeststatus,t.speed,t.totalamount
	,'0' as flag from request_main t where t.ownid=#{ownid,jdbcType=INTEGER}
	AND requeststatus !='7' AND requeststatus > '0'

		AND t.entitytype=#{entityType,jdbcType=INTEGER} 

	ORDER BY requesttime)
	dd    


          where code=#{billnumber,jdbcType=INTEGER}    
          and flag=#{flag,jdbcType=CHAR}

select

from request_main
WHERE
	(
		requestcode = #{id}
		AND LENGTH(#{id}) <= 10
	)
OR (
	id = #{id}
	AND LENGTH(#{id}) > 10
)

