mapper.select:
SELECT c.id, c.evaluate, c.catalogue, c.shortsubject, c.detinformation AS detinformation, c.fullsubject AS fulltitle, CONCAT_WS( '', c.shortsubject, c.fullsubject, c.enfullsubject, c.enshortsubject, c.commonlysubject, c.facevaluedis, c.sizedis, c.weightshow ), c.weightshow, c.facevaluedis FROM sys_suit_basic_info c WHERE 1 = 1
and c.years=#{years}
and c.country =#{country}
and c.facevaluedis =#{facevaluedis}
AND CONCAT_WS( '', c.years, c.shortsubject, c.fullsubject, c.enfullsubject, c.enshortsubject, c.commonlysubject, c.facevaluedis, c.sizedis, c.weightshow, c.detinformation ) LIKE CONCAT(CONCAT('%',#{item}),'%')
and c.catalogue LIKE CONCAT(CONCAT(#{catalogue}),'%')
and c.weight =#{weight}
and c.unitofmeasurement =#{unitofmeasurement}
and c.mquality =#{mquality}
and c.material=#{material}
and c.material >= 7 and c.material < 17
and c.material=#{material}
order by c.catalogue limit #{startRow},15
